import React from 'react';
import { useSelector } from "react-redux";
import { selectLessons } from "../../state/lessonSlice";
import {useParams} from "react-router-dom"; 
import './Lesson.css';

function LessonPage () {
    const {lessonid} = useParams();
 
   const Lessons = useSelector(selectLessons);

    return (
        <div
        style={{
          background: 'wheat',
           border: `6px solid red` ,
           height: '30rem',
           width: '40rem',
           paddingTop: '0rem',
           color: 'red',
           }}
        >
      <div className='lessonTitle'>{Lessons[lessonid].title}</div>
      <div className=''>by {Lessons[lessonid].author}</div>
      <div className='lessonText'>{Lessons[lessonid].text}</div>
      {/* 
       <div>{lesson.author}</div>
        */}
      </div>
    )
  
}
export default LessonPage;
