import React from 'react';
import { useSelector } from "react-redux";
import { selectLessons } from "../../state/lessonSlice";
import {useParams} from "react-router-dom"; 
import './Lesson.css';

function LessonPage () {
    const {lessonid} = useParams();
 
   const Lessons = useSelector(selectLessons);

    return (
    <div className='Lessons'>
        <br/>
        <div
        style={{
          background: 'wheat',
           border: `6px solid red` ,
           height: '30rem',
           width: '40rem',
           paddingTop: '0rem',
           //color: 'red',
           paddingTop: '2rem'
           }}
        >
       
      <div className='lessonTitle'>{Lessons[lessonid].title}
      &nbsp;`` by {Lessons[lessonid].author}
      </div>
      <div className='lessonText'></div>
      <div className='lessonText'>{Lessons[lessonid].text}</div>
      {/*  
       <div>{lesson.author}</div>
        */}
      </div>
  </div>
    )
  
}
export default LessonPage;
