import {Container, Row, Col} from 'react-bootstrap';
//import './gwScript.js';
import './Groupwork.css'

 function GroupBox() {

/*
 {Tasks.map((data, i)=> {
        return (
         <Row>
            <Col
          
          key={`task${i}`}
          > 
         {`Task ${i}`}

          </Col> 
       </Row>
       <Row>
            <Col
          
          key={`task${i}`}
          > 
         {`${data.info}`}

          </Col> 
       </Row>
       );
      })}

*/
